    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // se declara y define la letra ingresada
        String letra;
        letra=let.getText();
        char le;
        le=letra.charAt(0);
        
        //se define la palabra ingresada
        String cadena;
        cadena=palabra.getText();
        
        //se utiliza StringBuilder para dar funcionalidad al reemplazo
        StringBuilder nueva_cadena = new StringBuilder(cadena);
        nueva_cadena.setCharAt(2,le);
        
        // se convierte nuevamente en la palabra original
        cadena=nueva_cadena.toString();
        
        // se env√≠a a la ventana la palabra modificada
        nueva.setText(cadena);
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

