    private void IVAActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_IVAActionPerformed
        // IVA
        String valor;
        double salario,descuento,salario_desc;
        valor = sala.getText();
        salario = Double.parseDouble(valor);
        descuento = salario * 0.010;
        salario_desc = salario - descuento;
        resul.setText(String.valueOf(salario_desc));
    }//GEN-LAST:event_IVAActionPerformed

