    private void TodosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_TodosActionPerformed
        // TODOS
        String valor;
        double salario,descuento,descuent1,descuent2,descuent3,salario_desc;
        valor=sala.getText();
        salario=Double.parseDouble(valor);
        descuento = salario * 0.010;
        descuent1 = salario * 0.042;
        descuent2 = salario * 0.012;
        descuent3 = salario * 0.015;
        salario_desc = salario-descuento-descuent1-descuent2-descuent3;
        
        resul.setText(String.valueOf(salario_desc));
           
            
        
    }//GEN-LAST:event_TodosActionPerformed

