    private void txtinicialesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtinicialesActionPerformed
        // INICIALES
        String nom1,nom2,a1,a2,resultado,iniciales=" ";
        char letra1,letra2,letra3,letra4;
        
        nom1 = pnombre.getText();
        nom2 = snombre.getText();
        a1 = papellido.getText();
        a2 = sapellido.getText();
        
        letra1 =nom1.charAt(0);
        letra2 =nom2.charAt(0);
        letra3 =a1.charAt(0);
        letra4 =a2.charAt(0);
        
        iniciales = iniciales + letra1 + letra2 + letra3 + letra4;
        resultado = resul.getText();
        resultado = resultado + iniciales;
        resul.setText(resultado);
        
      
        
    }//GEN-LAST:event_txtinicialesActionPerformed

    private void txtultimasletrasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtultimasletrasActionPerformed
        // ULTIMAS LETRAS
     String nombre1,nombre2,apellido1,apellido2,resultado="",tot;
        int longi1,longi2,longi3,longi4,x,i,z,y;
        char letra1,letra2,letra3,letra4;
        
        nombre1=pnombre.getText();
        nombre2=snombre.getText();
        apellido1=papellido.getText();
        apellido2=sapellido.getText();
        
        longi1=nombre1.length();
        longi2=nombre2.length();
        longi3=apellido1.length();
        longi4=apellido2.length();
        
        x=longi1-1;
        i=longi2-1;
        z=longi3-1;
        y=longi4-1;
        
        letra1 =nombre1.charAt(x);
        letra2 =nombre2.charAt(i);
        letra3 =apellido1.charAt(z);
        letra4 =apellido2.charAt(y);
        
        resultado=resultado+letra1+letra2+letra3+letra4;
        tot=resul.getText();
        tot=tot+resultado;
        resul.setText(String.valueOf(tot));
        
        
        
    }//GEN-LAST:event_txtultimasletrasActionPerformed

    private void txtlongitudActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtlongitudActionPerformed
        // LONGITUD
        String num1,num2,tot="";
        int longi1,longi2;
        num1=pnombre.getText();
        num2=snombre.getText();
        longi1=num1.length();
        longi2=num2.length();
        if(longi1>longi2){
        tot=tot+num1;
        }else{
        tot=tot+num2;
        }resul.setText(String.valueOf(tot));
    }//GEN-LAST:event_txtlongitudActionPerformed

